import React, { useState, useRef } from 'react';
import { Grid, Checkbox, FormControlLabel, Typography } from '@mui/material';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const SortableList = () => {
    const [items, setItems] = useState([
        { id: 'receiveFile', label: 'Receive File', checked: false },
        { id: 'statisticsAndChecks', label: 'Statistics & Checks', checked: false },
        { id: 'columnValidation', label: 'Column Validation', checked: false },
        // Add more items here
    ]);

    const handleCheckboxChange = (id) => (event) => {
        setItems(items.map(item => item.id === id ? { ...item, checked: event.target.checked } : item));
    };

    const onDragEnd = (result) => {
        if (!result.destination) return;
        const newItems = Array.from(items);
        const [reorderedItem] = newItems.splice(result.source.index, 1);
        newItems.splice(result.destination.index, 0, reorderedItem);
        setItems(newItems);
    };

    return (
        <DragDropContext onDragEnd={onDragEnd}>
            <Droppable droppableId="droppable">
                {(provided) => (
                    <Grid {...provided.droppableProps} ref={provided.innerRef} container item xs={12} spacing={3} style={{ paddingLeft: '20px' }}>
                        {items.map((item, index) => (
                            <Draggable key={item.id} draggableId={item.id} index={index}>
                                {(provided) => (
                                    <Grid item ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps} style={{ marginLeft: '15px' }}>
                                        <FormControlLabel
                                            control={<Checkbox checked={item.checked} onChange={handleCheckboxChange(item.id)} color="primary" />}
                                            label={item.label}
                                        />
                                    </Grid>
                                )}
                            </Draggable>
                        ))}
                        {provided.placeholder}
                    </Grid>
                )}
            </Droppable>
        </DragDropContext>
    );
};

export default SortableList;
