        <Grid item xs={12}
              draggable
              onDragStart={(e) => onDragStart(e, index)}
              onDragOver={onDragOver}
              onDrop={(e) => onDrop(e, index)}
              style={{ cursor: 'grab' }}
        >
            <FormControlLabel
                control={
                    <Checkbox
                        checked={item.checked}
                        onChange={handleCheckboxChange(item.id)}
                        color="primary"
                    />
                }
                label={item.label}
            />
        </Grid>
    );

    // Splitting the items into two columns
    const halfwayIndex = Math.ceil(items.length / 2);
    const firstHalfItems = items.slice(0, halfwayIndex);
    const secondHalfItems = items.slice(halfwayIndex);

    return (
        <Grid container spacing={2} style={{ paddingLeft: '20px' }}>
            <Grid item xs={6}>
                {firstHalfItems.map(renderItem)}
            </Grid>
            <Grid item xs={6}>
                {secondHalfItems.map(renderItem)}
            </Grid>
        </Grid>
    );
};

export default SortableList;
