import React, { useState } from 'react';
import { Grid, Checkbox, FormControlLabel, Paper, IconButton } from '@mui/material';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';

const SortableList = () => {
    const initialItems = Array.from({ length: 20 }, (_, i) => ({
        id: `item-${i + 1}`,
        label: `Activity ${i + 1}`,
        checked: false
    }));

    const [items, setItems] = useState(initialItems);

    const handleCheckboxChange = (id) => (event) => {
        setItems(items.map(item => item.id === id ? { ...item, checked: event.target.checked } : item));
    };

    const onDragStart = (event, index) => {
        event.dataTransfer.setData("drag-item-index", index);
        event.dataTransfer.setData("drag-item-column", event.target.parentElement.getAttribute("data-column"));
    };

    const onDragOver = (event) => {
        event.preventDefault();
    };

    const onDrop = (event, dropIndex, dropColumn) => {
        event.preventDefault();
        const dragIndex = parseInt(event.dataTransfer.getData("drag-item-index"), 10);
        const dragColumn = event.dataTransfer.getData("drag-item-column");

        if (dragIndex === dropIndex && dragColumn === dropColumn) return;

        const newItems = [...items];
        const itemDragged = newItems[dragIndex];
        newItems.splice(dragIndex, 1); // Remove the dragged item

        if (dragColumn === dropColumn) {
            newItems.splice(dropIndex, 0, itemDragged); // Insert at the new index within the same column
        } else {
            // Adjust drop index based on column
            const adjustedDropIndex = dropColumn === 'second' ? dropIndex + Math.ceil(items.length / 2) : dropIndex;
            newItems.splice(adjustedDropIndex, 0, itemDragged); // Insert at the new index in the other column
        }

        setItems(newItems);
    };

    const renderItem = (item, index, column) => (
        <Grid item xs={12} key={item.id} draggable data-column={column}
              onDragStart={onDragStart}
              onDragOver={onDragOver}
              onDrop={(e) => onDrop(e, index, column)}
              style={{ cursor: 'pointer', display: 'flex', alignItems: 'center', marginBottom: '8px' }}
        >
            <Paper style={{ width: '100%', padding: '8px', boxSizing: 'border-box', display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <div>
                    <span style={{ marginRight: '8px' }}>{index + 1}.</span>
                    <FormControlLabel
                        control={<Checkbox checked={item.checked} onChange={handleCheckboxChange(item.id)} color="primary" />}
                        label={item.label}
                    />
                </div>
                <div>
                    <IconButton size="small" style={{ marginRight: '4px' }}><ArrowUpwardIcon /></IconButton>
                    <IconButton size="small"><ArrowDownwardIcon /></IconButton>
                </div>
            </Paper>
        </Grid>
    );

    const halfwayIndex = Math.ceil(items.length / 2);
    const firstHalfItems = items.slice(0, halfwayIndex);
    const secondHalfItems = items.slice(halfwayIndex);

    return (
        <Grid container spacing={2} style={{ paddingLeft: '20px', paddingTop: '20px' }}>
            <Grid item xs={6} onDragOver={onDragOver}>
                {firstHalfItems.map((item, index) => renderItem(item, index, 'first'))}
            </Grid>
            <Grid item xs={6} onDragOver={onDragOver}>
                {secondHalfItems.map((item, index) => renderItem(item, index, 'second'))}
            </Grid>
        </Grid>
    );
};

export default SortableList;
